import Actions from "../Action/Action"

export default function reducer(state={},action) {
    switch(action.type) {
        case Actions.ADD_ITEM : {
            return {...state, toDoArray: [...state.toDoArray, ...action.payload]}
        }
        case Actions.FIND_ITEM : {
            // const found = toDoArray.find(toDoArray => toDoArray.index = index);
            // console.log(found,'inside findvalue')
        
            //return {...state, toDoArray: [...state.toDoArray, action.payload]}
        }
        case Actions.UPDATE_ITEM : {
            const temVal = state.toDoArray.find((item,index) => {
                if (index==action.payload) {
                    return {...state, toDoArray: [...state.toDoArray.slice(0,action.payload),
                        {...temVal, arrcheck : !item.arrcheck} ]}        
                } 
                
            })
            // return {...state, toDoArray: [...state.toDoArray, ...action.payload]}
        }
        case Actions.DELETE_ITEM : {
            return {...state, toDoArray: [...state.toDoArray.slice(0,action.payload), 
              ...state.toDoArray.slice(action.payload+1)  ]}
        }
        default: {
            return state
        }
    }
}
