import Actions from "../Action/Action"

export default function reducer(state={},action) {
    switch(action.type) {
        case Actions.ADD_ITEM : {
            return {...state, toDoArray: [...state.toDoArray, ...action.payload]}
        }
        case Actions.UPDATE_ITEM : {
            return {...state, toDoArray: [...state.toDoArray, action.payload]}
        }
        case Actions.DELETE_ITEM : {
            return {...state, toDoArray: [...state.toDoArray, action.payload]}
        }
        default: {
            return state
        }
    }
}
