import React , { useState } from 'react'
import { connect } from 'react-redux' // to be able to connect to store

function AddToDoList (props) {
    const [inputItem , setInputItem] = useState({task:'',
                                                 subtask:'',
                                                 desc:'',
                                                 dt:'', 
                                                 arrcheck:'',
                                                 tstatus:''
                                                })

    const [updateIndex, setUpdateIndex] = useState(-1)
    var [updateCheck, setUpdateCheck] = useState({arrcheck:'',tstatus:''})


    var Handleclick=(ind)=>{
        var arrClick = props.tdArray[ind]
        var newStatus 
        console.log(arrClick,'arrClick')
            if (arrClick.tstatus=='Completed')
                {newStatus='Incomplete'}
            else
                {newStatus='Complete'}
            var newObj = (arrcheck=!arrClick.arrcheck,tstatus=newStatus)
            
            console.log(newObj,ind,'inside HandleClick')
            props.updateMyItem({ind: ind,obj: newObj })
    }

    var handleclear=()=> {
        console.log('handle clear')

        setInputItem({task:'',
        subtask:'',
        desc:'',
        dt:'', 
        arrcheck:'',
        tstatus:''}) 
    }
    var HandleAdd=(inputPara)=>{
        console.log('handle add and clear')
        if (updateIndex== -1 ) 
        {
            props.addMyList([inputPara])
        }
        else 
        {
            props.updateMyList({ind: updateIndex,obj:inputPara })
            setUpdateIndex(-1)
        }
        handleclear()
    }

    var HandleUpdate= (ind) => {
        var findItem = props.tdArray[ind]
        setInputItem(
            {task: findItem.task,
            subtask: findItem.subtask,
            desc: findItem.desc,
            dt: findItem.dt, 
            arrcheck: findItem.arrcheck,
            tstatus: findItem.tstatus
           }
        )
        setUpdateIndex(ind)
    }

    
    return(
        <>
            <div className='disptask'>
                <strong>Task</strong>
                <input type='text' value={inputItem.task} placeholder='Task'
                onChange={(event)=>{setInputItem({...inputItem,task: event.target.value})}} autoFocus/>
                <strong>Sub Task</strong>
                <input type = 'text' value = {inputItem.subtask} placeholder ='SubTask'
                onChange={(event)=>{setInputItem({...inputItem,subtask: event.target.value})}}/>
            </div>
            
            <div className='disptextarea'>
                <strong>Description</strong>
                <textarea  value = {inputItem.desc} rows="5" cols="70" 
                placeholder='Description' 
                onChange={(event)=>{setInputItem({...inputItem,desc: event.target.value})}}/>
            </div>
            
            <div className='dispdate' > 
                <strong>Date</strong>
                <input type = 'date' value = {inputItem.dt} 
                onChange={(event)=>{setInputItem({...inputItem,dt: event.target.value})}}/>
                <strong>Status</strong>
                <select value = {inputItem.tstatus} 
                onChange={(event)=>{setInputItem({...inputItem,tstatus: event.target.value,arrcheck:false })}}> 
                    <option value=''>Choose...</option>
                    <option value='Completed'>Completed	</option>
                   <option value='Incomplete'>Incomplete</option>
                </select>

            </div>
            
            {console.log(inputItem)}
            <p className='dispbutton'>
                <input type = 'button'  value ={updateIndex==-1 ? "Add Task" : "Update Task" } 
                      onClick={()=>{HandleAdd(inputItem)}} />
                    
            </p>
            
                {console.log(props.tdArray)}
    
               <ul>
                {props.tdArray.map((item, index) => (
                    <li  key={index} ><strong>
                        <input type="checkbox" id={index} name={index}
                        checked={item.arrcheck} onChange = {()=>{Handleclick(index)}}/>
                        {/* checked={item.tstatus=='Completed'? true: false} onChange = {()=>{}}/> */}
                        {`${item.task} ${item.subtask} ${item.desc} 
                        ${new Date(item.dt).toLocaleDateString('en-GB')}
                        ${item.tstatus}`}
                        <input type="button" value="Update" 
                        onClick = {()=>{HandleUpdate(index)}} />
                        <input type="button" value="Delete" 
                        onClick = {()=>{props.deleteMyList(index)}} />
                        {console.log(index,'is the index ')}
                        {console.log(props.tdArray)}                       
                        </strong>
                    </li>
                        ))}
                </ul>
        </>
    )
}

const mapStateToProps = (state) => {
    return{
        tdArray: state.toDoArray
    }
}

const mapDispatchToProps =(dispatch) => {
    return{
        addMyList: (val) => dispatch({type:'ADD_ITEM' , payload: val}) ,
        updateMyList: (valu) => dispatch({type:'UPDATE_ITEM' , payload: valu}) ,
        updateMyItem: (val) => dispatch({type:'UPDATE_SINGLE' , payload:val}),
        deleteMyList: (value) => dispatch({type:'DELETE_ITEM' , payload: value})
        }
}
export default connect(mapStateToProps,mapDispatchToProps)(AddToDoList)
