import React , { useState } from 'react'
import { connect } from 'react-redux' // to be able to connect to store

function AddToDoList (props) {
    const [inputItem , setInputItem] = useState({task:'',
                                                 subtask:'',
                                                 desc:'',
                                                 dt:'', 
                                                 tstatus:''
                                                })
    
    return(
        <>
            <div className='disptask'>
                <strong>Task</strong>
                <input type='text' value={inputItem.task} placeholder='Task'
                onChange={(event)=>{setInputItem({...inputItem,task: event.target.value})}} autoFocus/>
                <strong>Sub Task</strong>
                <input type = 'text' value = {inputItem.subtask} placeholder ='SubTask'
                onChange={(event)=>{setInputItem({...inputItem,subtask: event.target.value})}}/>
            </div>
            
            <div className='disptextarea'>
                <strong>Description</strong>
                <textarea  value = {inputItem.desc} rows="5" cols="70" 
                onChange={(event)=>{setInputItem({...inputItem,desc: event.target.value})}}/>
            </div>
            
            <div className='disptask' > 
                <strong>Actual Start Date</strong>
                <input type = 'date' value = {inputItem.dt} 
                onChange={(event)=>{setInputItem({...inputItem,dt: event.target.value})}}/>
            </div>
            
            {console.log(inputItem)}
            <p className='dispbutton'>
                <input type = 'button'  value ="Add Task" 
                onClick={()=>{props.addMyList([inputItem])}} />
            </p>
            <div text-align='centre'>    <strong>To Do List</strong> </div>
                {console.log(props.tdArray)}
               <ul>
                {props.tdArray.map((item, index) => (
                    <li key={index}><strong>
                        {item.task} {item.subtask} {item.desc}
                        {new Date(item.dt).toLocaleDateString('en-GB')}
                        {item.tstatus}
                        </strong></li>
                        ))}
                </ul>
        
        </>
    )
}

const mapStateToProps = (state) => {
    return{
        tdArray: state.toDoArray
    }
}

const mapDispatchToProps =(dispatch) => {
    return{
        addMyList: (val) => dispatch({type:'ADD_ITEM' , payload: val}) ,
        updateMyList: (val) => dispatch({type:'UPDATE_ITEM' , payload: val}) ,
        deleteMyList: (val) => dispatch({type:'DELETE_ITEM' , payload: val})
        }
}
export default connect(mapStateToProps,mapDispatchToProps)(AddToDoList)
